import { gql } from '@apollo/client';

export const CREATE_BOOK = gql`
mutation createBook(
  $title: String!,
  $published: Int!,
  $author: String!,
  $genres: [String!]!
  ) {
  addBook  {
    title
    published
    author
    genres
  }
}
`
