import React, {useEffect} from 'react'
import { useQuery, useMutation } from '@apollo/client';
import { USER_INFO, BOOKS_BY_GENRE } from "../queries"

const Recommended = ({ show }) => {
  const userResult = useQuery(USER_INFO)
  const [bookFromGenre, result] = useQuery(BOOKS_BY_GENRE)

  useEffect(() => {
    bookFromGenre({ variables: { genre: favouriteGenre }})
  }, [])

  if (!show) {
    return null
  }
  if (userResult.loading || result.loading)  {
    return <div>loading...</div>
  }

  const favouriteGenre = userResult.data.me.favouriteGenre
  const books = result.data ? result.data.allBooks : []


  return (
    <div>
      <h2>books</h2>
      <label>books in your favourite genre <b>{favouriteGenre}</b></label>
      <table>
        <tbody>
          <tr>
            <th></th>
            <th>
              author
            </th>
            <th>
              published
            </th>
          </tr>
          {books.map(book => {
            return (
            <tr key={book.title}>
            <td>{book.title}</td>
            <td>{book.author.name}</td>
            <td>{book.published}</td>
            </tr>
            ) 
          }
          )}
        </tbody>
      </table>
    </div>
  )
}

export default Recommended
