import { gql } from '@apollo/client';

const BOOK_DATA = gql`
fragment BookData on Book {
  title
  published
  author {
    name
    born
    id
  }
  genres
  id
}
`

export const ALL_AUTHORS = gql`
query {
  allAuthors  {
    name
    born
    id
    bookCount
  }
}
`
export const ALL_BOOKS = gql`
query {
  allBooks  {
    ...BookData
  }
  ${BOOK_DATA}
}
`
export const BOOKS_BY_GENRE = gql`
query bookFromGenre($genre: String!) {
  allBooks(genre: $genre)  {
    ...BookData
  }
  ${BOOK_DATA}
}
`

export const USER_INFO = gql`
query {
  me {
    username
    favouriteGenre
    id
  }
}
`
