import { gql } from '@apollo/client';

export const CREATE_BOOK = gql`
mutation createBook($title: String!, $published: Int!, $author: String!, $genres: [String!]!) {
  addBook  (
    title: $title,
    published: $published,
    author: $author,
    genres: $genres
  ) {
    title
    published
    author
    genres
    id
  }
}
`

export const SET_BIRTH = gql`
mutation setBirth($author: String!, $year: Int!) {
  editAuthor (
    name: $author,
    born: $year
  ) {
    name
    born
    id
    bookCount
  }
}
`
